INSERT INTO question_bank (admin_id, question_text, choices, correct_answer, difficulty, skill_name, grade, semester, subject, explanation, is_system_question, assessment_type) VALUES
((SELECT id FROM admins LIMIT 1), 'หนังสือ 4 ตั้ง ตั้งละ 5 เล่ม แจกเด็ก 2 คน คนละเท่าไร', '["8 เล่ม", "10 เล่ม", "12 เล่ม", "15 เล่ม"]', '10 เล่ม', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(4 \times 5) \div 2 = ?$ วิธีทำ: $20 \div 2 = 10$ คำอธิบาย: หาจำนวนหนังสือทั้งหมดก่อน (คูณ) แล้วแบ่งเท่าๆ กัน (หาร)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'เงิน 40 บาท ซื้อของ 8 บาท เหลือเงินแบ่งน้อง 4 คน คนละเท่าไร', '["6 บาท", "8 บาท", "10 บาท", "12 บาท"]', '8 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(40 - 8) \div 4 = ?$ วิธีทำ: $32 \div 4 = 8$ คำอธิบาย: หาเงินคงเหลือก่อน (ลบ) แล้วนำมาแบ่ง (หาร)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ขนม 8 ถุง แม่ให้อีก 6 ถุง นำไปขายถุงละ 4 บาท ได้เงินเท่าไร', '["48 บาท", "52 บาท", "56 บาท", "60 บาท"]', '56 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(8 + 6) \times 4 = ?$ วิธีทำ: $14 \times 4 = 56$ คำอธิบาย: หาจำนวนถุงทั้งหมดก่อน (บวก) แล้วคำนวณราคาขาย (คูณ)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'โดนัท 32 กล่อง กล่องละ 5 ชิ้น แจกไป 95 ชิ้น เหลือเท่าไร', '["60 ชิ้น", "65 ชิ้น", "70 ชิ้น", "75 ชิ้น"]', '65 ชิ้น', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(32 \times 5) - 95 = ?$ วิธีทำ: $160 - 95 = 65$ คำอธิบาย: หาจำนวนชิ้นทั้งหมดก่อน (คูณ) แล้วหักส่วนที่แจกออก (ลบ)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ดอกบัว 60 ดอก มัดกำละ 6 ดอก ขายกำละ 9 บาท ได้เงินเท่าไร', '["70 บาท", "80 บาท", "90 บาท", "100 บาท"]', '90 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(60 \div 6) \times 9 = ?$ วิธีทำ: $10 \times 9 = 90$ คำอธิบาย: หาจำนวนกำก่อน (หาร) แล้วคูณด้วยราคาขาย', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'เสื้อ 4 ตัว ตัวละ 105 บาท มีเงิน 280 บาท ต้องหาเพิ่มเท่าไร', '["120 บาท", "130 บาท", "140 บาท", "150 บาท"]', '140 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(4 \times 105) - 280 = ?$ วิธีทำ: $420 - 280 = 140$ คำอธิบาย: หาต้นทุนรวม (คูณ) แล้วลบด้วยเงินที่มีอยู่ เพื่อหาส่วนต่างที่ขาด', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ผ้า 75 ม. ซื้อเพิ่ม 20 ม. ตัดชุดชุดละ 5 ม. ได้กี่ชุด', '["15 ชุด", "19 ชุด", "20 ชุด", "25 ชุด"]', '19 ชุด', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(75 + 20) \div 5 = ?$ วิธีทำ: $95 \div 5 = 19$ คำอธิบาย: หาความยาวผ้ารวม (บวก) แล้วแบ่งตัด (หาร)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'จ่ายเหรียญสิบ 6 เหรียญ ซื้อของ 50 บาท ทอนเท่าไร', '["5 บาท", "10 บาท", "15 บาท", "20 บาท"]', '10 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(6 \times 10) - 50 = ?$ วิธีทำ: $60 - 50 = 10$ คำอธิบาย: หาจำนวนเงินที่จ่ายไปทั้งหมด (คูณ) แล้วลบราคาสินค้า', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ขนม 54 ชิ้น ใส่ถุงละ 9 ชิ้น ขายถุงละ 18 บาท ได้เงินเท่าไร', '["96 บาท", "108 บาท", "120 บาท", "132 บาท"]', '108 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(54 \div 9) \times 18 = ?$ วิธีทำ: $6 \times 18 = 108$ คำอธิบาย: หาจำนวนถุง (หาร) แล้วคูณราคาขาย', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ดินสอ 7 แท่ง แท่งละ 22 บาท ให้เงิน 180 บาท ทอนเท่าไร', '["24 บาท", "26 บาท", "28 บาท", "30 บาท"]', '26 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $180 - (7 \times 22) = ?$ วิธีทำ: $180 - 154 = 26$ คำอธิบาย: หาค่าสินค้า (คูณ) แล้วนำไปลบออกจากเงินที่จ่าย', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ไวโอลิน 3 แถว แถวละ 15 คน ขลุ่ย 6 คน เปียโน 2 คน รวมกี่คน', '["50 คน", "53 คน", "55 คน", "58 คน"]', '53 คน', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(3 \times 15) + 6 + 2 = ?$ วิธีทำ: $45 + 8 = 53$ คำอธิบาย: หาจำนวนคนเล่นไวโอลิน (คูณ) แล้วบวกกับคนที่เหลือ', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'กุหลาบ 140 บานชื่น 75 ตายไป 18 เหลือเท่าไร', '["185 ต้น", "190 ต้น", "197 ต้น", "200 ต้น"]', '197 ต้น', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(140 + 75) - 18 = ?$ วิธีทำ: $215 - 18 = 197$ คำอธิบาย: หาต้นไม้รวมก่อน (บวก) แล้วหักส่วนที่ตาย (ลบ)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'พ่อ 600 พ่อมากกว่าฉัน 450 แม่ให้อีก 60 ฉันมีเท่าไร', '["180 บาท", "200 บาท", "210 บาท", "220 บาท"]', '210 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(600 - 450) + 60 = ?$ วิธีทำ: $150 + 60 = 210$ คำอธิบาย: หาเงินของฉันก่อน (ลบเพราะน้อยกว่าพ่อ) แล้วบวกส่วนที่แม่ให้เพิ่ม', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ขนม 18 ชิ้น ชิ้นละ 8 บาท มีเงิน 250 บาท ทอนเท่าไร', '["96 บาท", "102 บาท", "106 บาท", "110 บาท"]', '106 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $250 - (18 \times 8) = ?$ วิธีทำ: $250 - 144 = 106$ คำอธิบาย: หาค่าขนมรวม (คูณ) แล้วนำไปลบออกจากเงินที่มี', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'น้ำปั่น 85 แก้ว แก้วละ 6 บาท ขายเสื้อได้อีก 2800 บาท รวมได้เงินเท่าไร', '["3200 บาท", "3210 บาท", "3250 บาท", "3310 บาท"]', '3310 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(85 \times 6) + 2800 = ?$ วิธีทำ: $510 + 2800 = 3310$ คำอธิบาย: หาเงินจากการขายน้ำ (คูณ) แล้วมารวมกับเงินขายเสื้อ (บวก)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ขนม 48 ไก่ 20 แจกเด็ก 4 คน คนละเท่าๆกัน ได้คนละกี่ชิ้น', '["15 ชิ้น", "17 ชิ้น", "19 ชิ้น", "21 ชิ้น"]', '17 ชิ้น', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(48 + 20) \div 4 = ?$ วิธีทำ: $68 \div 4 = 17$ คำอธิบาย: รวมขนมทั้งหมด (บวก) แล้วแบ่งเท่าๆ กัน (หาร)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ส้ม 6 กก. กก.ละ 35 บาท ให้เงิน 400 ทอนเท่าไร', '["180 บาท", "190 บาท", "200 บาท", "210 บาท"]', '190 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $400 - (6 \times 35) = ?$ วิธีทำ: $400 - 210 = 190$ คำอธิบาย: หาค่าส้ม (คูณ) แล้วลบออกจากเงินที่จ่าย', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ตุ๊กตา 5 ตัว ตัวละ 72 บาท ให้เงิน 450 ทอนเท่าไร', '["60 บาท", "70 บาท", "80 บาท", "90 บาท"]', '90 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $450 - (5 \times 72) = ?$ วิธีทำ: $450 - 360 = 90$ คำอธิบาย: หาค่าตุ๊กตา (คูณ) แล้วลบออกจากเงิน', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'นร. 60 หญิง 25 แบ่งชายกลุ่มละ 7 ได้กี่กลุ่ม', '["3 กลุ่ม", "4 กลุ่ม", "5 กลุ่ม", "6 กลุ่ม"]', '5 กลุ่ม', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(60 - 25) \div 7 = ?$ วิธีทำ: $35 \div 7 = 5$ คำอธิบาย: หาจำนวนนักเรียนชายก่อน (ลบ) แล้วจัดกลุ่ม (หาร)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ทุเรียน 6 กอง กองละ 9 ผล เพิ่ม 4 กอง กองละ 6 ผล รวมเท่าไร', '["72 ผล", "76 ผล", "78 ผล", "82 ผล"]', '78 ผล', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(6 \times 9) + (4 \times 6) = ?$ วิธีทำ: $54 + 24 = 78$ คำอธิบาย: หาจำนวนในแต่ละชุด (คูณ) แล้วนำมารวมกัน (บวก)', true, 'semester');
