INSERT INTO question_bank (admin_id, question_text, choices, correct_answer, difficulty, skill_name, grade, semester, subject, explanation, is_system_question, assessment_type) VALUES
((SELECT id FROM admins LIMIT 1), 'ส้ม 6 กอง กองละ 22 มะม่วง 4 กอง กองละ 28 ส้มมากกว่าเท่าไร', '["18 ผล", "20 ผล", "22 ผล", "24 ผล"]', '20 ผล', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(6 \times 22) - (4 \times 28) = ?$ วิธีทำ: $132 - 112 = 20$ คำอธิบาย: หาจำนวนผลไม้แต่ละชนิด (คูณ) แล้วหาผลต่าง (ลบ)', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ไอศกรีม 6 แท่ง แท่งละ 9 บาท ให้เงิน 120 ทอนเท่าไร', '["56 บาท", "60 บาท", "64 บาท", "66 บาท"]', '66 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $120 - (6 \times 9) = ?$ วิธีทำ: $120 - 54 = 66$ คำอธิบาย: หาค่าไอศกรีม (คูณ) แล้วลบออกจากเงิน', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ขนม 85 ให้ไป 20 ขายชิ้นละ 6 บาท ได้เงินเท่าไร', '["360 บาท", "380 บาท", "390 บาท", "400 บาท"]', '390 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(85 - 20) \times 6 = ?$ วิธีทำ: $65 \times 6 = 390$ คำอธิบาย: หาจำนวนที่เหลือ (ลบ) แล้วคำนวณเงิน (คูณ)', true, 'semester'),
