((SELECT id FROM admins LIMIT 1), 'ปิงปอง 60 ขาย 12 สั่งเพิ่ม 50 รวมเท่าไร', '["92 ลูก", "96 ลูก", "98 ลูก", "102 ลูก"]', '98 ลูก', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(60 - 12) + 50 = ?$ วิธีทำ: $48 + 50 = 98$ คำอธิบาย: หักส่วนที่ขาย (ลบ) บวกส่วนที่มาเพิ่ม', true, 'semester'),
