((SELECT id FROM admins LIMIT 1), 'ส้มโอ 140 ขาย 110 ซื้อเพิ่ม 180 รวมเท่าไร', '["200 ผล", "205 ผล", "210 ผล", "220 ผล"]', '210 ผล', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $(140 - 110) + 180 = ?$ วิธีทำ: $30 + 180 = 210$ คำอธิบาย: หักส่วนที่ขาย (ลบ) บวกส่วนที่ซื้อมาใหม่', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'น้ำเขียว 25 แดงมากกว่าเขียว 12 ส้มน้อยกว่าแดง 6 ส้มกี่ขวด', '["28 ขวด", "30 ขวด", "31 ขวด", "33 ขวด"]', '31 ขวด', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'วิธีทำ: แดง = $25 + 12 = 37$ ส้ม = $37 - 6 = 31$ คำอธิบาย: หาจำนวนตามเงื่อนไขทีละขั้น (มากกว่า=บวก, น้อยกว่า=ลบ)', true, 'semester'),
