((SELECT id FROM admins LIMIT 1), 'สติ๊กเกอร์ 180 ให้เพื่อนห้องเดียวกัน 42 ต่างห้อง 85 เหลือเท่าไร', '["48 อัน", "51 อัน", "53 อัน", "56 อัน"]', '53 อัน', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $180 - 42 - 85 = ?$ วิธีทำ: $138 - 85 = 53$ คำอธิบาย: ลบจำนวนที่ให้ออกไปทั้งสองครั้ง', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'นุ่น 150 อั้มมากกว่านุ่น 18 พลอยน้อยกว่าอั้ม 25 พลอยเท่าไร', '["138 บาท", "140 บาท", "143 บาท", "145 บาท"]', '143 บาท', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'วิธีทำ: อั้ม = $150 + 18 = 168$ พลอย = $168 - 25 = 143$ คำอธิบาย: คำนวณตามลำดับความสัมพันธ์', true, 'semester'),
((SELECT id FROM admins LIMIT 1), 'ดินสอ 600 บริจาค 300 บริจาค 250 เหลือเท่าไร', '["40 แท่ง", "45 แท่ง", "50 แท่ง", "55 แท่ง"]', '50 แท่ง', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $600 - 300 - 250 = ?$ วิธีทำ: $300 - 250 = 50$ คำอธิบาย: หักยอดบริจาคออกทีละรายการ', true, 'semester'),
