((SELECT id FROM admins LIMIT 1), 'สมุด 50 + 180 + 105 รวมเท่าไร', '["325 เล่ม", "330 เล่ม", "335 เล่ม", "340 เล่ม"]', '335 เล่ม', 'medium', 'การบวกลบคูณหาร ระคน', 2, 1, 'math', 'ประโยคสัญลักษณ์: $50 + 180 + 105 = ?$ คำอธิบาย: หาผลรวมทั้งหมด', true, 'semester'),
